import React from 'react';
import Axios from 'axios';
import $ from 'jquery';
import DataTable from 'datatables.net-dt';
import DownIcon from '../../resources/Robot/icon-download.svg';

$.DataTable = DataTable;

export class EventDataTable extends React.Component {
    constructor(props) {
        super(props);
        // this.myRef = React.createRef();
    }

    componentDidMount(nextProps, nextState) {
        if (this.props.reload) {
            $(this.refs.main).DataTable().ajax.reload();
        } else {
            Axios.get('http://localhost:4000/datatable/events/' + this.props.sn + '/limit')
                .then(res => {
                    console.log(res);
                    this.table = $(this.refs.main).DataTable({
                        data: res.data,
                        process: true,
                        searching: false,
                        paging: false,
                        lengthChange: false,
                        bInfo: false,
                        scrollXInner: '550px',
                        scrollX: '530px',
                        scrollY: false,
                        order: [[0, 'desc']],
                        columns: [
                            {"data": "occurrence_time"},
                            {"data": "code"},
                            {"data": "down"}
                        ],
                        language: {
                            "emptyTable": "저장된 이벤트 데이터가 없습니다."
                        }
                    });
                    this.table.rows().every((index) => {
                        let str = this.table.cell(index, 2).data();
                        this.table.cell(index, 2).data(str + '<img alt="" src=' + DownIcon + '></a>');
                    });
                    console.log(this.table);
                }).catch(e => {
                alert(e);
            }).finally(() => {

            });
        }
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        console.log(this.table);
        console.log(this.refs.main);
        let table = $(this.refs.main).DataTable();
        console.log(table);
        table.clear();
        //this.table.rows.add(this.transform(this.props.data));
        table.draw();
    }

    componentWillUnmount() {
        $('.event_history')
            .find('dataTable')
            .DataTable()
            .destroy(true);
    }

    render() {
        return (
            <table id="dataTable" className="" ref='main'>
                <thead>
                <tr>
                    <th>DATE</th>
                    <th>EVENT</th>
                    <th>LOG</th>
                </tr>
                </thead>
            </table>
        );
    }
}
