import React from "react";
import ReactDataTable from 'react-data-table-component';
import './DataTable.css';
import $ from 'jquery';
import DataTable from 'datatables.net';
/*<th>SN</th>
<th>Model</th>
<th>Site</th>
<th>Company</th>
<th>KPI</th>
<th>Header</th>
<th>State</th>
<th> Enter</th>*/
$.DataTable = DataTable;

const data = [
        {id: 1, title: 'Happy', year: '1992'},
        {id: 2, title: 'fucking', year: '1994'}
    ];

const columns = [
    {
        name: 'Title',
        selector: 'title',
        sortable: true,
    },
    {
        name: 'Year',
        selector: 'year',
        sortable: true,
        right: true,
    },
];

class DataTableComponent extends React.Component {
    constructor(props) {
        super(props);

    }

    componentDidMount(nextProps, nextState) {
        this.table = $(this.refs.main).DataTable({
            dom: '<"data-table-wrapper"tip>',
            data: data,
            columns,
            language: {
                info: 'Mostrando _START_-_END_ de _TOTAL_ puntos',
                infoEmpty: 'No hay puntos',
                paginate: {
                    next: 'Siguiente',
                    previous: 'Anterior',
                },
            }
        })
    }

    componentWillUpdate() {
        this.table.clear();
        this.table.rows.add(this.transform(this.props.data));
        this.table.draw();
    }

    componentWillUnmount() {
        $('.data-table-wrapper')
            .find('table')
            .DataTable()
            .destroy(true);
    }

    render() {
        return (
            <div className="list_contents_table">
                <table id="dataTable" className="table_style">
                    <thead>
                    <tr>
                        <th>id</th>
                        <th>title</th>
                        <th>year</th>
                    </tr>
                    </thead>
                </table>
                <div>
                </div>
                <table className="table" cellSpacing="0" ref='main'/>
                <ReactDataTable
                    title="NRmK"
                    columns={columns}
                    data={data}
                />
            </div>
        );
    }
}

export default DataTableComponent;
