import React from "react";
import ReactDataTable from 'react-data-table-component';
import './DataTable.css';
import $ from 'jquery';
import DataTable from 'datatables.net-dt';

$.DataTable = DataTable;

const data = [
    {id: '1', title: 'Happy', year: '1992'},
    {id: '2', title: 'fucking', year: '1994'}
];

const columns = [
    {
        name: 'Title',
        selector: 'title',
        sortable: true,
    },
    {
        name: 'Year',
        selector: 'year',
        sortable: true,
        right: true,
    },
];

class DataTableComponent extends React.Component {
    constructor(props) {
        super(props);

    }

    componentDidMount(nextProps, nextState) {
        this.table = $(this.refs.main).DataTable({
            data: [
                {sn: 1234, model: 'A', site: 'A', company: 'A', kpi: 'A', header:'A', state:'A', enter:'<a href="#">'},
                {sn: 2425, model: 'B', site: 'B', company: 'A', kpi: 'A', header:'B', state:'B', enter:'<a href="#">'},
                {sn: 7547, model: 'A', site: 'C', company: 'A', kpi: 'A', header:'C', state:'C', enter:'<a href="#">'},
                {sn: 9636, model: 'A', site: 'D', company: 'A', kpi: 'A', header:'D', state:'D', enter:'<a href="#">'},
                {sn: 1131, model: 'B', site: 'E', company: 'A', kpi: 'A', header:'E', state:'E', enter:'<a href="#">'},
            ],
            columns: [
                {data: 'sn'},
                {data: 'model'},
                {data: 'site'},
                {data: 'company'},
                {data: 'kpi'},
                {data: 'header'},
                {data: 'state'},
                {data: 'enter'},

            ]
        })
    }

    componentWillUpdate() {
        this.table.clear();
        this.table.rows.add(this.transform(this.props.data));
        this.table.draw();
    }

    componentWillUnmount() {
        $('.data-table-wrapper')
            .find('table')
            .DataTable()
            .destroy(true);
    }

    render() {
        return (
            <div className="list_contents_table">
                <table id="dataTable" className="table_style" ref='main'>
                    <thead>
                    <tr>
                        <th>SN</th>
                        <th>Model</th>
                        <th>Site</th>
                        <th>Company</th>
                        <th>KPI</th>
                        <th>Header</th>
                        <th>State</th>
                        <th> Enter</th>
                    </tr>
                    </thead>
                </table>
            </div>
        );
    }
}

export default DataTableComponent;
