import React from "react";
import ReactDataTable from 'react-data-table-component';
import './DataTable.css';
import $ from 'jquery';
import DataTable from 'datatables.net';
/*<th>SN</th>
<th>Model</th>
<th>Site</th>
<th>Company</th>
<th>KPI</th>
<th>Header</th>
<th>State</th>
<th> Enter</th>
<ReactDataTable
                    title="NRmK"
                    columns={columns}
                    data={data}
                />
*/
$.DataTable = DataTable;

const data = [
    {id: '1', title: 'Happy', year: '1992'},
    {id: '2', title: 'fucking', year: '1994'}
];

const columns = [
    {
        name: 'Title',
        selector: 'title',
        sortable: true,
    },
    {
        name: 'Year',
        selector: 'year',
        sortable: true,
        right: true,
    },
];

class DataTableComponent extends React.Component {
    constructor(props) {
        super(props);

    }

    componentDidMount(nextProps, nextState) {
        this.table = $(this.refs.main).DataTable({
            data: [
                [
                    "Tiger Nixon",
                    "System Architect",
                    "Edinburgh",
                    "5421",
                    "2011/04/25",
                    "$3,120"
                ],
                [
                    "Garrett Winters",
                    "Director",
                    "Edinburgh",
                    "8422",
                    "2011/07/25",
                    "$5,300"
                ]
            ],
            columns: [
                
            ]
        })
    }

    componentWillUpdate() {
        this.table.clear();
        this.table.rows.add(this.transform(this.props.data));
        this.table.draw();
    }

    componentWillUnmount() {
        $('.data-table-wrapper')
            .find('table')
            .DataTable()
            .destroy(true);
    }

    render() {
        return (
            <div className="list_contents_table">
                <table id="dataTable" className="table_style" ref='main'>
                    <thead>
                    <tr>
                        <th>id</th>
                        <th>title</th>
                        <th>year</th>
                    </tr>
                    </thead>
                </table>
            </div>
        );
    }
}

export default DataTableComponent;
