import React from 'react';
import Chart from 'chart.js';

let myLineChart;

// Chart.default.global.defaultFontFamily = "'PT Sans', sans-serif";
// Chart.default.global.legend.display = false;

export default class LineGraph extends React.PureComponent {
    chartRef = React.createRef();

    componentDidMount() {
        this.buildChart();
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        this.buildChart();
    }

    buildChart = () => {
        const myChartRef = this.chartRef.current.getContext("2d");
        const {data, average, labels} = this.props;

        if (typeof myLineChart !== "undefined") myLineChart.destroy();

        myLineChart = new Chart(myChartRef, {
            type: 'line',
            data: {
                // Bring in data
                //labels: ['a', 'b', 'c', 'd', 'e', 'f'],
                datasets: [
                    {
                        label: "Sales",
                        //data: [16, 46, 23, 72, 55, 78],
                        data: [{'x': '11-06 03:16:00', 'y': 53},
                        {'x': '11-06 03:15:00', 'y': 11},
                        {'x': '11-06 03:14:00', 'y': 34},
                        {'x': '11-06 03:13:00', 'y': 78}],
                        fill: false,
                        borderColor: "#6610f2"
                    },
                    {
                        label: "National Average",
                        //data: [77, 42, 12, 33, 55, 88],
                        data: [{'x': '11-06 03:16:00', 'y': 34},
                        {'x': '11-06 03:15:00', 'y': 21},
                        {'x': '11-06 03:14:00', 'y': 66},
                        {'x': '11-06 03:13:00', 'y': 54}],
                        fill: false,
                        borderColor: "#E0E0E0"
                    }
                ]
            },
            options: {
                //Customize chart options
                legend: {
                    position: "bottom"
                },
                tooltips: {
                    mode: 'index',
                    intersect: false,
                },
                hover: {
                    mode: 'nearest',
                    intersect: false,
                    animationDuration: 0
                },
                scales: {
                    xAxes: [{
                        //barThickness: 30,
                        type: 'time',
                        time: {
                            padding: 1,
                            fontStyle: "bold",
                            parser: 'MM-DD HH:mm:ss',
                            unit: 'minute',
                            unitStepSize: 30,
                            displayFormat: {
                                hour: 'MM-DD HH:mm'
                            }
                        },
                        ticks: {
                            autoSkip: true,
                            source: 'auto'
                        }
                    }],
                }
            }
        })
    };

    render() {
        return (
            <div className="chart_section">
                <h3>WorK Count [CNT]</h3>
                <div className="chart_cnt">
                    <canvas id="myCountChart" ref={this.chartRef}/>
                </div>
            </div>
        );
    }
}
