import React from 'react';
import Chart from 'chart.js';

let myBarChart;

// Chart.default.global.defaultFontFamily = "'PT Sans', sans-serif";
// Chart.default.global.legend.display = false;

export default class BarGraph extends React.PureComponent {
    chartRef = React.createRef();

    componentDidMount() {
        this.buildChart();
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        this.buildChart();
    }

    buildChart = () => {
        const myChartRef = this.chartRef.current.getContext("2d");
        const {data} = this.props;

        if (typeof myBarChart !== "undefined") myBarChart.destroy();

        myBarChart = new Chart(myChartRef, {
            type: 'bar',
            data: {
                // Bring in data
                labels: ['a', 'b', 'c', 'd', 'e', 'f'],
                // labels: labels
                datasets: [
                    {
                        label: "Sales",
                        data: [16, 46, 23, 72, 55, 78],
                        fill: true,
                        borderColor: "#6610f2"
                    },
                    {
                        label: "National Average",
                        data: [77, 42, 12, 33, 55, 88],
                        fill: true,
                        borderColor: "#E0E0E0"
                    }
                ]
            },
            options: {
                //Customize chart options
                legend: {
                    position: "bottom"
                },
                tooltips: {
                    mode: 'index',
                    intersect: false,
                },
                hover: {
                    mode: 'nearest',
                    intersect: false,
                    animationDuration: 0
                },
                // scales: {
                //     xAxes: [{
                //         //barThickness: 30,
                //         type: 'time',
                //         time: {
                //             padding: 15,
                //             fontStyle: "bold",
                //             parser: 'MM-DD HH:mm:ss',
                //             unit: 'minute',
                //             unitStepSize: 1,
                //             displayFormat: {
                //                 hour: 'MM-DD HH:mm'
                //             }
                //         },
                //         ticks: {
                //             autoSkip: true,
                //             source: 'auto'
                //         }
                //     }],
                // }
            }
        })
    };

    render() {
        return (
            <div className="chart_section">
                <h3>Temperature [TMP]</h3>
                <div className="chart_tmp">
                    <canvas id="myTempChart" ref={this.chartRef}/>
                </div>
            </div>
        );
    }
}
